mapper.select:
select n.*, c.notice_content from tb_notice n inner join tb_notice_content c on n.notice_code = c.notice_code where
n.store_no = #{storeNo}
n.user_no = #{userNo}
order by n.reg_date desc limit 70

mapper.update:
		update tb_notice
  		   set status = 1
         where 

         		store_no = #{storeNo}


         		user_no = #{userNo}

mapper.select:
		select count(*)
		  from tb_notice
		 where 

         		store_no = #{storeNo}


         		user_no = #{userNo}

           and status = 0

mapper.delete:
		delete
		  from tb_notice
		 where 

         		store_no = #{storeNo}


         		user_no = #{userNo}
