mapper.update:
		update tb_review
			set status = #{status}
		where review_no in 

                #{no}

mapper.select:
select u.nick_name nickName, r.review_no reviewNo, s.store_name storeName,
r.reg_date regDate, r.review_content reviewContent, (select u.nick_name from tb_user u where rr.user_no = u.user_no) as reportNickname, (select count(*) from tb_report_review rr where rr.review_no = r.review_no) as reportCount from tb_review r join tb_report_review rr on r.review_no = rr.review_no join tb_user u on r.user_no = u.user_no join tb_store s on r.store_no = s.store_no;
order by rr.report_no desc;
